CREATE TABLE IF NOT EXISTS employees (
    empid   SERIAL PRIMARY KEY,
    profile TEXT DEFAULT 'data:image/jpeg;base64,',
    fullname    VARCHAR(100) NOT NULL,
    designation VARCHAR(100),
    department  VARCHAR(100),
    salary  NUMERIC(10, 2) CHECK (salary >= 0),
    role    VARCHAR(50) NOT NULL DEFAULT 'employee',
    created_at  TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at  TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

INSERT INTO employees (empid, profile, fullname, designation, department, salary, role)
SELECT * FROM (
    SELECT 1, '', 'Devi', 'Software Engineer', 'IT/Infrastructure', 70000.00, 'admin'
    UNION ALL
    SELECT 2, '', 'Siva', 'Tech Manager', 'IT/Infrastructure', 100000.00, 'admin'
) AS new_employees
WHERE NOT EXISTS (SELECT 1 FROM employees);
